mapper.select:
        SELECT


--         级联表的字段

        FROM wuzi  a



                and a.id in

                #{item}



                and a.wuzi_name like CONCAT('%',#{params.wuziName},'%')


                and a.wuzi_types = #{params.wuziTypes}


                  and a.wuzi_kucun_number >= #{params.wuziKucunNumberStart}   


                  and a.wuzi_kucun_number <= #{params.wuziKucunNumberEnd}   


                and a.wuzi_kucun_number = #{params.wuziKucunNumber}


                and a.wuzi_danwei like CONCAT('%',#{params.wuziDanwei},'%')


                  and a.wuzi_delete >= #{params.wuziDeleteStart}   


                  and a.wuzi_delete <= #{params.wuziDeleteEnd}   


                and a.wuzi_delete = #{params.wuziDelete}


                and a.wuzi_content like CONCAT('%',#{params.wuziContent},'%')




        order by a.${params.orderBy} desc
